package 복습;

public class 기본형심화 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 기본형 4가지
		// 정수, 실수, 문자, 논리
		// int, double, char, boolean
		double temp = 1.2;
		int floor = 3;
		char color = 'b';
		boolean juice = false;
		
		String location = "선릉역";
		
		System.out.println("오늘 온도는 " + temp + "도");
		System.out.println("층수는 " + floor);
		System.out.println("좋아하는 색깔의 앞글자는 " + color);
		System.out.println("아침에 쥬스를 마셨나?" + juice);
		System.out.println("멀티캠퍼스 위치는?" + location);
	}

}
