package 복습;

public class 배열기본확인문제2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] places = {"america", "japan", "Italy", "spain", "mexico"};
		for (String x : places) {
			System.out.println(x);
		}
		char[] colors = {'r', 'b', 'g', 'p', 'y'};
		for (char x : colors) {
			System.out.println(x);
		}
		double[] heights = {187.7, 159.3, 163.4, 167.5, 185.0};
		for (double x : heights) {
			System.out.println(x);
		}
	}

}
